#!/bin/bash

# This script generates an endemism layer using data generated by the biocache and loads it into the spatial portal

export DATE=$(date +built_%y%m%d)
export PTH="/data/ala/data/layers"
export JAVA_CLASSPATH="$PTH/process/layer-ingestion-1.0-SNAPSHOT/layer-ingestion-1.0-SNAPSHOT.jar:$PTH/process/layer-ingestion-1.0-SNAPSHOT/lib/*"
export GEOSERVER_USRPWD="user:password"
export GEOSERVER_URL="http://localhost:8082/geoserver"
export OUTPUTDIR="$PTH/process/endemism/$DATE"
export GDAL_TRANSLATE="/data/ala/utils/gdal-1.9.0/apps/gdal_translate"

# Create directory to store output files:
mkdir $OUTPUTDIR 

echo $DATE > $OUTPUTDIR/build.log

# download generated data from the biocache
echo "downloading data from biocache" >> $OUTPUTDIR/build.log 
wget http://biocache.ala.org.au/archives/exports/cell-species-lists-0.1-degree.txt 1>> $OUTPUTDIR/build.log 2>&1
wget http://biocache.ala.org.au/archives/exports/species-cell-counts-0.1-degree.txt 1>> $OUTPUTDIR/build.log 2>&1
wget http://biocache.ala.org.au/archives/exports/cell-species-lists-0.1-degree-non-marine.txt 1>> $OUTPUTDIR/build.log 2>&1
wget http://biocache.ala.org.au/archives/exports/species-cell-counts-0.1-degree-non-marine.txt 1>> $OUTPUTDIR/build.log 2>&1
mv *.txt $OUTPUTDIR

# generate ASCII grid and DIVA grid
echo "generating grid files" 1>> $OUTPUTDIR/build.log
java -Xmx3G -cp ${JAVA_CLASSPATH} org.ala.spatial.analysis.layers.EndemismLayerGenerator 0.1 $OUTPUTDIR/species-cell-counts-0.1-degree.txt $OUTPUTDIR/cell-species-lists-0.1-degree.txt $OUTPUTDIR endemism  1>> $OUTPUTDIR/build.log 2>&1
java -Xmx3G -cp ${JAVA_CLASSPATH} org.ala.spatial.analysis.layers.EndemismLayerGenerator 0.1 $OUTPUTDIR/species-cell-counts-0.1-degree-non-marine.txt $OUTPUTDIR/cell-species-lists-0.1-degree-non-marine.txt $OUTPUTDIR endemism_non_marine  1>> $OUTPUTDIR/build.log 2>&1


cp $OUTPUTDIR/endemism.gr* $PTH/ready/diva 1>> $OUTPUTDIR/build.log 2>&1
cp $OUTPUTDIR/endemism_non_marine.gr* $PTH/ready/diva 1>> $OUTPUTDIR/build.log 2>&1

#Fix mode and ownership of diva grids
chmod 777 $PTH/ready/diva/* 1>> $OUTPUTDIR/build.log 2>&1
chown tomcat:wheel $PTH/ready/diva/* 1>> $OUTPUTDIR/build.log 2>&1

# Generate geotiff
echo "generating geotiff" >> $OUTPUTDIR/build.log
$GDAL_TRANSLATE -of GTiff -ot Float32 -a_srs EPSG:4326 $OUTPUTDIR/endemism.asc $OUTPUTDIR/endemism.tif 1>> $OUTPUTDIR/build.log 2>&1
cp $OUTPUTDIR/endemism.tif $PTH/ready/geotiff 1>> $OUTPUTDIR/build.log 2>&1
$GDAL_TRANSLATE -of GTiff -ot Float32 -a_srs EPSG:4326 $OUTPUTDIR/endemism_non_marine.asc $OUTPUTDIR/endemism_non_marine.tif 1>> $OUTPUTDIR/build.log 2>&1
cp $OUTPUTDIR/endemism_non_marine.tif $PTH/ready/geotiff 1>> $OUTPUTDIR/build.log 2>&1

#Fix mode and ownership of geotiffs
chmod 777 $PTH/ready/geotiff/* 1>> $OUTPUTDIR/build.log 2>&1
chown tomcat:wheel $PTH/ready/geotiff/* 1>> $OUTPUTDIR/build.log 2>&1

# Generate legend
echo "generating legend" >> $OUTPUTDIR/build.log
java -Xmx3G -cp ${JAVA_CLASSPATH} org.ala.layers.legend.GridLegend $PTH/ready/diva/endemism $PTH/test/endemism 8 1 1>> $OUTPUTDIR/build.log 2>&1
java -Xmx3G -cp ${JAVA_CLASSPATH} org.ala.layers.legend.GridLegend $PTH/ready/diva/endemism_non_marine $PTH/test/endemism_non_marine 8 1 1>> $OUTPUTDIR/build.log 2>&1

# Upload to geoserver
echo "uploading to geoserver" 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: text/plain"  -d "file://$PTH/ready/geotiff/endemism.tif" $GEOSERVER_URL/rest/workspaces/ALA/coveragestores/endemism/external.geotiff 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPOST -H "Content-type: text/xml"  -d "<style><name>endemism_style</name><filename>endemism.sld</filename></style>"  $GEOSERVER_URL/rest/styles 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: application/vnd.ogc.sld+xml"  -d @$PTH/test/endemism.sld $GEOSERVER_URL/rest/styles/endemism_style 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: text/xml"   -d "<layer><enabled>true</enabled><defaultStyle><name>endemism_style</name></defaultStyle></layer>" $GEOSERVER_URL/rest/layers/ALA:endemism 1>> $OUTPUTDIR/build.log

curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: text/plain"  -d "file://$PTH/ready/geotiff/endemism_non_marine.tif" $GEOSERVER_URL/rest/workspaces/ALA/coveragestores/endemism_non_marine/external.geotiff 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPOST -H "Content-type: text/xml"  -d "<style><name>endemism_non_marine_style</name><filename>endemism_non_marine.sld</filename></style>"  $GEOSERVER_URL/rest/styles 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: application/vnd.ogc.sld+xml"  -d @$PTH/test/endemism_non_marine.sld $GEOSERVER_URL/rest/styles/endemism_non_marine_style 1>> $OUTPUTDIR/build.log
curl -u $GEOSERVER_USRPWD -XPUT -H "Content-type: text/xml"   -d "<layer><enabled>true</enabled><defaultStyle><name>endemism_non_marine_style</name></defaultStyle></layer>" $GEOSERVER_URL/rest/layers/ALA:endemism_non_marine 1>> $OUTPUTDIR/build.log

# Regenerate layer analysis and distances
echo "regenerating layer analysis and distances" 1>> $OUTPUTDIR/build.log
rm -f /data/ala/data/layers/analysis/0.5/el1055.gr* 1>> $OUTPUTDIR/build.log 2>&1
rm -f /data/ala/data/layers/analysis/0.5/el1056.gr* 1>> $OUTPUTDIR/build.log 2>&1
rm -f /data/ala/data/layers/analysis/0.01/el1055.gr* 1>> $OUTPUTDIR/build.log 2>&1
rm -f /data/ala/data/layers/analysis/0.01/el1056.gr* 1>> $OUTPUTDIR/build.log 2>&1
rm -f /data/ala/data/layers/analysis/0.0025/el1055.gr* 1>> $OUTPUTDIR/build.log 2>&1
rm -f /data/ala/data/layers/analysis/0.0025/el1056.gr* 1>> $OUTPUTDIR/build.log 2>&1
cp /data/ala/data/alaspatial/layerDistances.properties /data/ala/data/alaspatial/layerDistances.properties_old 1>> $OUTPUTDIR/build.log 2>&1
sed -i '/el1055\|el1056/d' /data/ala/data/alaspatial/layerDistances.properties 1>> $OUTPUTDIR/build.log 2>&1

sh $PTH/process/layer-ingestion-1.0-SNAPSHOT/environmental_background_processing.sh 1>> $OUTPUTDIR/build.log 2>&1

echo "finished" 1>> $OUTPUTDIR/build.log

cp $OUTPUTDIR/build.log /data/ala/runtime/output/endemism.log
